                    { "IQA0246", "Mandatory", "Boolean", null, "3", "C8", "IQT0020", "Is it possible that a child might need to be treated urgently as part of the project before it is possible to identify and seek consent from a person with parental rights and responsibility or another legal representative?", "Boolean", null, null },
                    { "IQA0247", "Conditional", "Text", null, "3a", "C8", "IQT0020", "Justify the need for urgent treatment in this project. What arrangements will be put in place to identify and seek consent from a person with parental rights and responsibility or another legal representative, once urgent actions have been completed?", "Text", null, null },
                    { "IQA0248", "Conditional", "Text", null, "4a", "C8", "IQT0020", "What arrangements will you make to seek consent to continue in the project when participating children are able to give consent for themselves?", "Text", null, null },
                    { "IQA0249", "Conditional", "Text", null, "4b", "C8", "IQT0020", "What arrangements will you make to seek consent to continue in the trial when participating children reach the age of 16?", "Text", null, null },
